def hasCycle(head):
    slow = fast = head

    while fast and fast.next:
        slow = slow.next        # 1 step
        fast = fast.next.next   # 2 steps

        if slow == fast:
            return True

    return False

binary-search.code:
def search(nums, target):
    left, right = 0, len(nums) - 1

    while left <= right:
        mid = (left + right) // 2

        if nums[mid] == target:
            return mid
        elif nums[mid] < target:
            left = mid + 1   # search right
        else:
            right = mid - 1  # search left

    return -1

number-of-islands.code:
def numIslands(grid):
    if not grid:
        return 0

    count = 0

    def dfs(i, j):
        if i < 0 or i >= len(grid) or j < 0 or j >= len(grid[0]):
            return
        if grid[i][j] != '1':
            return

        grid[i][j] = '0'  # mark visited
        dfs(i+1, j)  # down
        dfs(i-1, j)  # up
        dfs(i, j+1)  # right
        dfs(i, j-1)  # left

    for i in range(len(grid)):
        for j in range(len(grid[0])):
            if grid[i][j] == '1':
                dfs(i, j)
                count += 1

    return count

